// D√©bogage global pour d√©tecter les objets rendus dans React

// Intercepter console.error pour d√©tecter les erreurs d'objets
const originalConsoleError = console.error;
console.error = (...args) => {
  const message = args[0];
  if (typeof message === 'string' && message.includes('Objects are not valid as a React child')) {
    console.error('üö® ERREUR D\'OBJET D√âTECT√âE:', ...args);
    console.trace('Stack trace complet:');
  }
  originalConsoleError.apply(console, args);
};

// Fonction pour v√©rifier les donn√©es avant le rendu
export const checkDataBeforeRender = (data, context = 'Unknown') => {
  if (typeof data === 'object' && data !== null && !Array.isArray(data)) {
    console.warn(`üîç Objet d√©tect√© avant rendu dans ${context}:`, data);
    
    // V√©rifier si c'est un objet probl√©matique
    if (data.nom || data.code || data._id) {
      console.error(`üö® OBJET PROBL√âMATIQUE D√âTECT√â dans ${context}:`, data);
      console.trace('Stack trace:');
      return false;
    }
  }
  return true;
};

// Fonction pour s√©curiser les donn√©es avant le rendu
export const secureDataForRender = (data, context = 'Unknown') => {
  if (typeof data === 'object' && data !== null && !Array.isArray(data)) {
    console.warn(`üîß S√©curisation d'un objet dans ${context}:`, data);
    
    // Retourner une repr√©sentation s√©curis√©e
    if (data.nom) return data.nom;
    if (data.code) return data.code;
    if (data._id) return data._id;
    if (data.name) return data.name;
    
    return '[Objet]';
  }
  
  return data;
};
